package Statment;

public class RelationalOprator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


		//		a<b -less than
		//		a>b -greater than
		//		a<=b-less than equal to
		//		a>=b-greater than equal to
		//		a==b -equal to
		//		a!=b=not equal to


		int a=100;
		int	b=100;

		if(a!=b) {

			System.out.println("a is not equal to b");
		}else
		{
			System.out.println("a is equal to b");

		}
		String p="Hello";
		String k="hello";
		if(p==k) {

			System.out.println("p is not equal to k");
		}else
		{
			System.out.println("p is equal to k for 1st way");

		}

		if(p.equals(k)) {

			System.out.println("p is not equal to k");
		}else
		{
			System.out.println("p is wswdxDD equal to k for 1st way");

		}

		if(p.equalsIgnoreCase(k)) {

			System.out.println("p is not equal to k");
		}else
		{
			System.out.println("p is equal SXAWDXdx WSX to k for 1st way");

		}

	}

}
