package operator;

public class LogicalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a=10;
		int b=5;
		int c=20;
		
		
		
		//logical opertor &&= if 1st condition is false, second condition will not  check
		System.out.println(a<b && a<c);
	
	//Bitwise operator -it will check all conf=ditions
		System.out.println(a<b & a<c);
		
	
	
	
	}

}
