package operator;

public class UnaryOpertors {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=4;
		System.out.println(i);
		System.out.println(i++);
		System.out.println(i++);
		System.out.println(i);
		System.out.println(++i);
		System.out.println(i--);
		System.out.println(i);
		System.out.println(--i);
		
		
	}

}
