package operator;

public class Unaryoperssd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=10;
		
		
		System.out.println(a++);
		System.out.println(a++ + ++a);
		System.out.println(b++ + b++);
		System.out.println(b);
		System.out.println(a++ + b++);
		
		
		
		
		
	}

}
